import React from 'react';
import { AbsoluteFill, useCurrentFrame, useVideoConfig, spring, interpolate, Sequence } from 'remotion';

const SCENES = [
  { title: 'Ship AI Apps', subtitle: 'in minutes, not months', bg: '#0f0f1a' },
  { title: 'Any Model', subtitle: 'GPT-4 ¬∑ Claude ¬∑ Llama ¬∑ Mistral', bg: '#0a1628' },
  { title: 'Scale to Zero', subtitle: 'Pay only when your app runs', bg: '#0f0f1a' },
  { title: 'One Command Deploy', subtitle: 'infsh app deploy', bg: '#0a1628' },
];

const FEATURES = [
  { icon: '‚ö°', label: 'GPU & CPU Runtimes' },
  { icon: 'üîó', label: 'API + SDK + Embed' },
  { icon: 'üõ°Ô∏è', label: 'Built-in Auth & Billing' },
  { icon: 'üåç', label: 'Edge-Ready CDN' },
];

const SCENE_DURATION = 75;

const FadeSlide: React.FC<{ children: React.ReactNode; direction?: 'up' | 'down'; delay?: number; sceneDuration?: number }> = ({ children, direction = 'up', delay = 0, sceneDuration = SCENE_DURATION }) => {
  const frame = useCurrentFrame();
  const { fps } = useVideoConfig();
  const localFrame = frame - delay;
  const enter = spring({ frame: Math.max(0, localFrame), fps, config: { damping: 15, stiffness: 80 } });
  const exitStart = sceneDuration - 20;
  const exit = localFrame > exitStart ? interpolate(localFrame, [exitStart, exitStart + 12], [1, 0], { extrapolateLeft: 'clamp', extrapolateRight: 'clamp' }) : 1;
  const yDir = direction === 'up' ? 1 : -1;
  const y = interpolate(enter, [0, 1], [50 * yDir, 0]);
  return (
    <div style={{ transform: `translateY(${y}px)`, opacity: enter * exit }}>
      {children}
    </div>
  );
};

const SceneTitle: React.FC<{ title: string; subtitle: string; accent: string }> = ({ title, subtitle, accent }) => (
  <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', gap: 20 }}>
    <FadeSlide delay={0}>
      <div style={{ fontSize: 88, fontWeight: 800, color: '#fff', fontFamily: 'system-ui, -apple-system, sans-serif', letterSpacing: '-0.03em', textAlign: 'center' }}>{title}</div>
    </FadeSlide>
    <FadeSlide delay={8} direction="down">
      <div style={{ fontSize: 36, fontWeight: 400, color: accent, fontFamily: 'system-ui, sans-serif', letterSpacing: '0.02em', textAlign: 'center' }}>{subtitle}</div>
    </FadeSlide>
  </div>
);

const FeatureGrid: React.FC<{ accent: string }> = ({ accent }) => (
  <div style={{ display: 'flex', gap: 32, flexWrap: 'wrap', justifyContent: 'center', maxWidth: 900 }}>
    {FEATURES.map((f, i) => (
      <FadeSlide key={i} delay={i * 6} direction="up">
        <div style={{ display: 'flex', alignItems: 'center', gap: 14, backgroundColor: 'rgba(255,255,255,0.06)', borderRadius: 16, padding: '18px 28px', border: '1px solid rgba(255,255,255,0.08)' }}>
          <span style={{ fontSize: 32 }}>{f.icon}</span>
          <span style={{ fontSize: 24, color: '#fff', fontWeight: 500, fontFamily: 'system-ui, sans-serif' }}>{f.label}</span>
        </div>
      </FadeSlide>
    ))}
  </div>
);

const CTAScene: React.FC<{ accent: string; url: string }> = ({ accent, url }) => {
  const frame = useCurrentFrame();
  const { fps } = useVideoConfig();
  const pulse = 1 + Math.sin(frame * 0.08) * 0.03;
  const btnSpring = spring({ frame: Math.max(0, frame - 15), fps, config: { damping: 12 } });
  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', gap: 40 }}>
      <FadeSlide delay={0} sceneDuration={SCENE_DURATION + 15}>
        <div style={{ fontSize: 76, fontWeight: 800, color: '#fff', fontFamily: 'system-ui, sans-serif', textAlign: 'center', lineHeight: 1.15 }}>Start Building<br />Today</div>
      </FadeSlide>
      <div style={{ transform: `scale(${btnSpring * pulse})`, opacity: btnSpring }}>
        <div style={{ background: `linear-gradient(135deg, ${accent}, #a855f7)`, borderRadius: 60, padding: '22px 56px', boxShadow: `0 0 40px ${accent}44` }}>
          <span style={{ fontSize: 28, fontWeight: 700, color: '#fff', fontFamily: 'system-ui, sans-serif' }}>{url}</span>
        </div>
      </div>
    </div>
  );
};

const TopBar: React.FC<{ accent: string }> = ({ accent }) => {
  const frame = useCurrentFrame();
  const { durationInFrames } = useVideoConfig();
  const progress = interpolate(frame, [0, durationInFrames], [0, 100], { extrapolateRight: 'clamp' });
  return (
    <div style={{ position: 'absolute', top: 0, left: 0, right: 0, height: 3, backgroundColor: 'rgba(255,255,255,0.05)', zIndex: 10 }}>
      <div style={{ width: `${progress}%`, height: '100%', background: `linear-gradient(90deg, ${accent}, #a855f7)` }} />
    </div>
  );
};

const Logo: React.FC<{ accent: string }> = ({ accent }) => {
  const frame = useCurrentFrame();
  const { fps } = useVideoConfig();
  const enter = spring({ frame, fps, config: { damping: 20 } });
  return (
    <div style={{ position: 'absolute', bottom: 36, right: 44, opacity: enter * 0.5, zIndex: 10 }}>
      <span style={{ fontSize: 18, color: accent, fontFamily: 'monospace', fontWeight: 600, letterSpacing: '0.05em' }}>inference.sh</span>
    </div>
  );
};

const Main: React.FC<{ accent?: string; url?: string }> = ({ accent = '#00d4ff', url = 'inference.sh' }) => {
  const frame = useCurrentFrame();
  const bgIdx = Math.min(Math.floor(frame / SCENE_DURATION), SCENES.length - 1);
  const bg = SCENES[bgIdx].bg;

  return (
    <AbsoluteFill style={{ backgroundColor: bg }}>
      <TopBar accent={accent} />

      {SCENES.map((scene, i) => (
        <Sequence key={i} from={i * SCENE_DURATION} durationInFrames={SCENE_DURATION}>
          <AbsoluteFill style={{ justifyContent: 'center', alignItems: 'center' }}>
            <SceneTitle title={scene.title} subtitle={scene.subtitle} accent={accent} />
          </AbsoluteFill>
        </Sequence>
      ))}

      <Sequence from={SCENES.length * SCENE_DURATION} durationInFrames={SCENE_DURATION}>
        <AbsoluteFill style={{ justifyContent: 'center', alignItems: 'center', backgroundColor: '#0f0f1a' }}>
          <FeatureGrid accent={accent} />
        </AbsoluteFill>
      </Sequence>

      <Sequence from={(SCENES.length + 1) * SCENE_DURATION} durationInFrames={SCENE_DURATION + 15}>
        <AbsoluteFill style={{ justifyContent: 'center', alignItems: 'center', backgroundColor: '#0a0a1a' }}>
          <CTAScene accent={accent} url={url} />
        </AbsoluteFill>
      </Sequence>

      <Logo accent={accent} />
    </AbsoluteFill>
  );
};

export default Main;
