import React from 'react';
import { AbsoluteFill, useCurrentFrame, useVideoConfig, spring, interpolate } from 'remotion';

const Main: React.FC<{ title: string; color: string }> = ({ title = 'Hello World', color = '#e94560' }) => {
  const frame = useCurrentFrame();
  const { fps } = useVideoConfig();
  const scale = spring({ frame, fps, config: { damping: 20 } });
  const opacity = interpolate(scale, [0, 1], [0, 1]);
  return (
    <AbsoluteFill style={{ backgroundColor: '#1a1a2e', justifyContent: 'center', alignItems: 'center' }}>
      <div style={{ color, fontSize: 80, fontWeight: 700, transform: `scale(${scale})`, opacity, fontFamily: 'sans-serif' }}>
        {title}
      </div>
    </AbsoluteFill>
  );
};

export default Main;
